import React, { useEffect } from "react";
import { connect, StringCodec } from "nats.ws";

function App() {
  useEffect(() => {
    let nc; // NATS bağlantısını tutacak değişken

    const connectToNats = async () => {
      try {
        // NATS sunucusuna bağlan
        nc = await connect({
          servers: "ws://localhost:8080", // TLS olmadan bağlantı

        });
        console.log("Connected to NATS!");

        // String codec oluştur
        const sc = StringCodec();

        // Belirli bir konuya abone ol
        const sub = nc.subscribe("test.subject");
        console.log("Subscribed to 'test.subject'");

        // Mesajları dinle
        (async () => {
          for await (const msg of sub) {
            console.log(`Received message: ${sc.decode(msg.data)}`);
          }
        })();
      } catch (error) {
        console.error("Error connecting to NATS:", error);
      }
    };

    connectToNats();

    // Cleanup: Bağlantıyı bileşen kaldırıldığında kapat
    return () => {
      if (nc) {
        nc.close();
        console.log("NATS connection closed.");
      }
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>NATS WebSocket Test</h1>
        <p>Listening to NATS messages...</p>
      </header>
    </div>
  );
}

export default App;
